/*
Module: Onboarding and Timecard
Last Updated: Lilian (4/2/2025), Lilian (2/1/2025)
High level steps:
Part 1:
1. Login as Hiring Manager.
2. For first time user, need to click on Agree with terms button.
2. Go to menu Create Offer - https://app.uat2-extremereach.com/fo/offer-details
3. Choose Project - Productions ABC.
4. Fill in all the required details and click Add & Send Offer button.
Part 2:
1. Login as Admin, go to menu Administrations > Customers - https://app.uat2-extremereach.com/adbridge/cs/customer/details/167721/users
2. Find the Payee email address and click UAT button until it turn green.
3. Login as admin and go to menu Administrations > Notifications - https://app.uat2-extremereach.com/adbridge/cs/support/notificationsearch?notificationDateStart=12%2F03%2F2024%2000%3A00%3A00
4. Find the email for the offer created.
5. Once click View, click on the Complete Registeration button.
6. Reset the password for the Payee email login.
*/
// Part 1
// Step 1: Login as Hiring Manager
cy.userLoginNonAdmin();
cy.visit(`${this.postfixURL.offer}`);
cy.get('[data-cy="username"]', { timeout: 20000 }).type(this.users.hiringmanagerEmail);
cy.get('[data-cy="next"]', { timeout: 20000 }).click();
cy.get('[data-cy="password"]', { timeout: 20000 }).type(this.users.hiringmanagerPassword);
cy.get('[data-cy="login"]', { timeout: 20000 }).click({ force: true });
cy.acceptTerms();
cy.get('.spinner-threedots', { timeout: 60000 }).should('not.be.visible');
// Step 2: Fill up offering details
// Project name
cy.get('kendo-textbox[formcontrolname="firstName"]', { timeout: 20000 }).should('be.visible');
cy.get('[textfield="projectName"]').type(this.projectDetails.projectName, { delay: 100 });
cy.get('[textfield="projectName"]').type('{enter}');
// First name
cy.get('kendo-textbox[formcontrolname="firstName"] input').type(this.users.payeeFirstName);
// Last name
cy.get('kendo-textbox[formcontrolname="lastName"] input').type(this.users.payeeLastName);
// Email address
cy.get('kendo-textbox[formcontrolname="emailAddress"] input').type(this.users.payeeEmail);
// Employment status
cy.get('kendo-dropdownlist[formcontrolname="employmentStatus"] button.k-input-button').click();
cy.get('kendo-dropdownlist[formcontrolname="employmentStatus"] button.k-input-button').type(this.payeeDetails.employmentStatus);
cy.get('kendo-dropdownlist[formcontrolname="employmentStatus"] button.k-input-button').type('{enter}');
// Start date
cy.get('kendo-dateinput input#datepicker-1').clear();
cy.get('kendo-dateinput input#datepicker-1').type('11-11-2023');
// End date
cy.get('kendo-dateinput input#datepicker-2').clear();
cy.get('kendo-dateinput input#datepicker-2').type('10-10-2024');
// Hire state
cy.get('kendo-combobox[formcontrolname="hireState"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="hireState"] button.k-input-button').type(this.payeeDetails.hireState);
cy.get('kendo-combobox[formcontrolname="hireState"] button.k-input-button').type('{enter}');
// Hire city/country
cy.get('kendo-combobox[formcontrolname="hireCity"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="hireCity"] button.k-input-button').type(this.payeeDetails.hireCity);
cy.get('kendo-combobox[formcontrolname="hireCity"] button.k-input-button').type('{enter}');
// Department
cy.get('kendo-combobox[formcontrolname="department"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="department"] button.k-input-button').type(this.payeeDetails.department);
cy.get('kendo-combobox[formcontrolname="department"] button.k-input-button').type('{enter}');
// Union
cy.get('kendo-combobox[formcontrolname="union"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="union"] button.k-input-button').type(this.payeeDetails.union);
cy.get('kendo-combobox[formcontrolname="union"] button.k-input-button').type('{enter}');
// Occupation
cy.get('kendo-combobox[formcontrolname="occupation"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="occupation"] button.k-input-button').type(this.payeeDetails.occupation);
cy.get('kendo-combobox[formcontrolname="occupation"] button.k-input-button').type('{enter}');
// O.T. Formula Rate
cy.get('kendo-combobox[formcontrolname="otFormulaRuleId"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="otFormulaRuleId"] button.k-input-button').type(this.payeeDetails.OTformulaRate);
cy.get('kendo-combobox[formcontrolname="otFormulaRuleId"] button.k-input-button').type('{enter}');
// Rate Based on
cy.get('kendo-combobox[formcontrolname="rateBasedOn"] button.k-input-button').click();
cy.get('kendo-combobox[formcontrolname="rateBasedOn"] button.k-input-button').type(this.payeeDetails.rateBasedOn);
cy.get('kendo-combobox[formcontrolname="rateBasedOn"] button.k-input-button').type('{enter}');
// Pay rate
cy.get('kendo-numerictextbox[formcontrolname="nonUnionRate"] input.k-input-inner').clear();
cy.get('kendo-numerictextbox[formcontrolname="nonUnionRate"] input.k-input-inner').type(this.payeeDetails.payRate);
cy.get('.spinner-threedots', { timeout: 60000 }).should('not.be.visible');
// Step 3: Save and send offer
cy.get('button.btn.btn-primary', { timeout: 15000 }).contains('Add & Send Offer').click();
cy.get('.k-notification-content', { timeout: 15000 }).should('contain.text', 'Offer email has been sent successfully');
// Step 4: Check offer is created
cy.visit(`${this.postfixURL.allOffer}`);
cy.get('.spinner-threedots', { timeout: 60000 }).should('not.be.visible'); // Wait for page to complete load
cy.get('input[placeholder="Search Payee Name"]', { timeout: 60000 }).type(this.users.payeeLastName);
cy.get('button[icon="search"]').click();
cy.get('td[data-kendo-grid-column-index="0"]').first().should('contain.text', `${this.users.payeeLastName}`); // Offer status
cy.get('td[data-kendo-grid-column-index="9"]').first().should('contain.text', 'Pending Payee'); // Offer status
cy.get('td[data-kendo-grid-column-index="10"]').first().should('contain.text', 'Pending Payee'); // I-9 status
// Step 5: Logout
cy.logout();
// Part 2:
// Step 1: Login as Admin and navigate to Administration > Customer page
cy.userLogin();
cy.visit(`${this.postfixURL.adminCustomer}`);
// Step 2: Enable UAT button to green
cy.get('.spinner-threedots', { timeout: 60000 }).should('not.be.visible'); // Wait for page to complete load
cy.get('[data-cy="add-new-user-button"]', { timeout: 20000 }).should('be.visible');
cy.checkAndEnableUATAccess(this.users.payeeEmail);
// Step 2: Navigate to Administration > Notification
cy.visit(`${this.postfixURL.searchNotification}`);
// Step 3: Complete the registration
cy.completeRegistration(this.users.payeeFirstName, this.users.payeeLastName, this.users.payeePassword, this.users.payeeEmail);
cy.userLoginNonAdmin();
cy.get('[data-cy="username"]', { timeout: 30000 }).should('be.visible').type(this.users.payeeEmail);
cy.get('[data-cy="next"]', { timeout: 30000 }).should('be.visible').click();
cy.get('[data-cy="password"]', { timeout: 30000 }).should('be.visible').type(this.users.payeePassword);
cy.get('[data-cy="login"]', { timeout: 20000 }).click({ force: true });
// For first time logging in for payee
cy.wait(5000);
cy.acceptPayeeTerms();

/*
Module: Onboarding and Timecard
Last Updated: Lilian (9/4/2025)
High level steps:
1. Login as new payee with new password.
2. For first time user, need to click on Accept button on terms and condition panel.
3. Go to https://app.uat2-extremereach.com/fo/payee-offers and click Review button.
4. Check content of offer.
5. Click Accept and Begin Start button.
6. Fill in all mandatory info in the Payee Wizard until the last steps.
7. Sign the document on the SignNow popup window."
*/
// Step 1: Login as New Payee
cy.userLoginNonAdmin();
cy.visit(`${this.postfixURL.payeeOffer}`);
cy.get('[data-cy="username"]').type(this.users.payeeEmail);
cy.get('[data-cy="next"]').click();
cy.get('[data-cy="password"]').type(this.users.payeePassword);
cy.get('[data-cy="login"]').click();
// Step 2: Accept agreement
cy.acceptPayeeTerms();
cy.get('td[data-kendo-grid-column-index="0"]', { timeout: 9000 }).first().should('contain.text', this.projectDetails.customerName);
cy.get('td[data-kendo-grid-column-index="1"]').first().should('contain.text', this.projectDetails.projectName);
cy.get('td[data-kendo-grid-column-index="2"]').first().should('contain.text', this.payeeDetails.occupation);
// BUG XRPCF-810: SOMETIMES APPEAR, SOMETIMES NOT
// Uncomment this section of code once the bug is fixed
// We do not want the rest of the test case fail due to this incorrect status
// cy.get('td[data-kendo-grid-column-index="3"]').first().should('contain.text','Individual');
// cy.get('td[data-kendo-grid-column-index="4"]').first().should('contain.text','Pending Payee');
// cy.get('td[data-kendo-grid-column-index="5"]').first().should('contain.text','Pending Payee');
cy.contains('button', 'Review').first().click();
// Step 4: Check content
// Header
cy.contains('strong', this.projectDetails.projectName).should('exist');
cy.contains('strong', this.users.hiringmanagerFirstName).should('exist');
cy.contains('strong', this.users.hiringmanagerLastName).should('exist');
cy.contains('p', this.users.hiringmanagerEmail).should('exist');
// Department
cy.contains('label', 'Department')
    .invoke('attr', 'for')
    .then((id) => {
    cy.get(`input#${id}`)
        .invoke('val')
        .then((value) => {
        expect(value).to.equal(this.payeeDetails.department);
    });
});
// Occupation
cy.contains('label', 'Occupation')
    .invoke('attr', 'for')
    .then((id) => {
    cy.get(`input#${id}`)
        .invoke('val')
        .then((value) => {
        expect(value).to.equal(this.payeeDetails.occupation);
    });
});
// Union
cy.contains('label', 'Union')
    .invoke('attr', 'for')
    .then((id) => {
    cy.get(`input#${id}`)
        .invoke('val')
        .then((value) => {
        expect(value).to.equal(this.payeeDetails.union);
    });
});
// Rate
cy.contains('label', 'Rate')
    .parents('.row')
    .find('input.k-input-inner')
    .invoke('val')
    .then((value) => {
    expect(value).to.equal('1000');
});
// Step 5: Click Accept and Begin Start button
cy.contains('button', ' Accept & Begin Start Packet ').click();
// Step 6: Fill up mandatory fields
// Defect - https://app.extremereach.com/cs/tickets/details/582633/details
const socialSecurityNumber = `${faker_1.faker.string.numeric(3)}-${faker_1.faker.string.numeric(2)}-${faker_1.faker.string.numeric(4)}`;
const phoneNumber = faker_1.faker.phone.number('(###) ###-####');
// Part 1
// Check button is disabled when not all mandatory fields are filled up
// First Name
cy.get('input[name="firstName"]').clear().type(this.users.payeeFirstName);
// Last Name
cy.get('input[name="lastName"]').clear().type(this.users.payeeLastName);
// Email
cy.get('input[name="emailAddress"]').clear().type(this.users.payeeEmail);
// Phone Number
cy.get('input[name="phoneNumber"]').clear().type(phoneNumber);
// Social security number
cy.get('input[name="ssn"]').clear().type(socialSecurityNumber);
// Date of birth
cy.get('input[id="datepicker-1"]').clear().type(this.payeeDetails.dateOfBirth);
// Address 1
cy.get('input[name="homeAddress1"][type="text"]').clear().type(this.payeeDetails.address1);
// Address 2
cy.get('input[name="homeAddress2"][type="text"]').clear().type(this.payeeDetails.address2);
// City
cy.get('input[name="homeCity"]').clear().type(this.payeeDetails.homeCity);
// State
cy.contains('label', 'State *').parent().find('kendo-dropdownlist').click();
cy.contains('label', 'State *').parent().find('kendo-dropdownlist').type(this.payeeDetails.state);
// Postal Code
cy.get('input[name="postalCode"]').clear();
cy.get('input[name="postalCode"]').type(this.payeeDetails.postalCode);
// Mailing Address - Use same as home address
cy.get('input[name="homeAddress1"][type="checkbox"]').then(($checkbox) => {
    if (!$checkbox.is(':checked')) {
        cy.wrap($checkbox).click();
    }
});
// Gender
cy.contains('label', 'Gender').parent().find('kendo-dropdownlist').click();
cy.contains('label', 'Gender').parent().find('kendo-dropdownlist').type(this.payeeDetails.gender);
cy.contains('label', 'Ethnicity').parent().find('kendo-dropdownlist').click();
cy.contains('label', 'Ethnicity').parent().find('kendo-dropdownlist').type(this.payeeDetails.ethnicity);
// Workaround
cy.get('input[name="ssn"]').clear().type(socialSecurityNumber);
cy.get('input[name="emailAddress"]').clear().type(this.users.payeeEmail);
cy.get('input[name="firstName"]').click();
cy.get('button[name="search"]').should('be.enabled').click();
// Part 2
// Maritial Status
cy.contains('label', 'Marital Status').parent().find('kendo-dropdownlist').type(this.payeeDetails.maritalStatus);
// I claim exemption from withholding for 2023
cy.get('input[name="exception"][type="checkbox"]').eq(0).click();
// I have two jobs with similar pay
cy.get('input[name="twoJobs"][type="checkbox"]').click();
// State Filling Status
// Defect caps - 582642 -fixed
cy.contains('label', 'State Filing Status * ').parent().find('select').select('Single');
cy.get('button[name="search"]').should('be.enabled');
cy.get('button[name="search"]').click();
// Part 3
// Last Name - Not able to detect input values
// Citizenship
cy.get('[id="workPermitType"][value="CitizenOfTheUnitedStates"]').click();
// Document List
cy.contains('label', 'Document List').parent().find('kendo-dropdownlist').click();
cy.contains('label', 'Document List').parent().find('kendo-dropdownlist').type(this.payeeDetails.documentList);
cy.contains('label', 'Document List').parent().find('kendo-dropdownlist').type('{enter}');
// Document A Title
cy.contains('label', 'Document A Title').parent().find('kendo-dropdownlist').click();
cy.contains('label', 'Document A Title').parent().find('kendo-dropdownlist').type(this.payeeDetails.documentATitle);
cy.contains('label', 'Document A Title').parent().find('kendo-dropdownlist').type('{enter}');
const filePath = 'dummyDocumentA.txt';
cy.contains('label', 'Upload a copy of Document A *') // Find the label
    .parent() // Traverse to the parent containing the input
    .find('input[type="file"]') // Locate the file input field
    .attachFile(filePath);
cy.get('button[name="search"]').should('be.enabled');
cy.get('button[name="search"]').click();
cy.get('div[role="alert"]', { timeout: 60000 }).should('be.visible').and('contain.text', 'Document uploaded.');
// Part 4
// Accept
cy.get('body').then(($body) => {
    if ($body.find('span:contains("Accept")').length > 0) {
        // If the element exists, click it
        cy.contains('span', 'Accept', { timeout: 60000 }).click();
    }
});
// Direct Deposit
cy.get('input[id="directDepositNo"][type="radio"]').click();
// Enroll in XR Payee Portal
cy.get('input[id="termsAndConditionNo"][type="radio"]').click();
cy.get('button[name="search"]').should('be.enabled');
cy.get('button[name="search"]').click();
//  I affirm that I have reviewed the information above and everything is correct
cy.contains('span', 'I affirm that I have reviewed the information above and everything is correct.', { timeout: 5000 })
    .parent()
    .find('input[type="checkbox"]')
    .click();
// I affirm that I have reviewed the forms required by California Department of Labor
cy.contains('span', 'I affirm that I have reviewed the forms required by California Department of Labor.')
    .parent()
    .find('input[type="checkbox"]')
    .click();
cy.get('button[name="search"]').should('be.enabled');
cy.get('button[name="search"]').click();
cy.get('span.k-icon.k-i-loading', { timeout: 5000 }).should('be.visible');
cy.get('span.k-icon.k-i-loading', { timeout: 60000 }).should('not.exist');
cy.signNow(this.users.payeeFirstName, true);

/*
Module: Onboarding and Timecard
Last Updated: Lilian (17/4/2025)
High level steps:
1. Login as Admin and go to  https://app.uat2-extremereach.com/fo/purchase-order/admin
2. Select Admin Action as Manage Onboarding Approval.
3. Search for Customer - Regression Test Customer, Project - AUTOBOTS XRKL PRODUCTION.
4. Fill in the approval level. Use the reviewer credentials (email) and to which level they can approve.
5. Click Save.
*/
cy.userLogin();
cy.purchaseOrderAdminManageOnboardingApproval({
    useFaker: true,
    visitPurchaseOrderAdminPage: '/fo/purchase-order/admin',
    selectAdminAction: 'Manage Onboarding Approvals',
    selectCustomer: this.projectDetails.customerName,
    selectProject: this.projectDetails.projectName
});
const poAdmin = new core_payroll_1.PurchaseOrderAdmin();
poAdmin.clickSearch();
const approvalLevel = '1';
const role = 'Production Manager';
const user = this.users.reviewerEmail;
const formTypes = ['Deal Memo', 'Offer', 'XR Start Form', 'Wage Theft Form'];
const departments = ['Costume', 'Grips', 'Office', 'Production'];
const requirebeforeRouting = 'False';
const sendToXRAfterApproval = 'True';
cy.get('body').then(($body) => {
    if ($body.find('tr.k-grid-norecords').length > 0 && $body.find('tr.k-grid-norecords').text().includes('No records available')) {
        cy.log('No records available row detected.');
        cy.writeFile('src/fixtures/tc106-result.json', { isEmpty: true });
    }
    else {
        let hasMismatch = false;
        // Approval Level
        cy.get('td[aria-colindex="1"]')
            .invoke('text')
            .then((text) => {
            if (text.trim() !== approvalLevel) {
                hasMismatch = true;
                cy.log(`Mismatch in Approval Level: expected "${approvalLevel}", got "${text.trim()}"`);
            }
        });
        // Role
        cy.get('td[aria-colindex="2"]')
            .invoke('text')
            .then((text) => {
            if (text.trim() !== role) {
                hasMismatch = true;
                cy.log(`Mismatch in Role: expected "${role}", got "${text.trim()}"`);
            }
        });
        // User
        cy.get('td[aria-colindex="3"] .k-chip-label')
            .invoke('text')
            .then((text) => {
            if (text.trim() !== user) {
                hasMismatch = true;
                cy.log(`Mismatch in User: expected "${user}", got "${text.trim()}"`);
            }
        });
        // Form Types
        cy.get('td[aria-colindex="4"] .k-chip-label').then((chips) => {
            const actualFormTypes = Cypress._.map(chips, (chip) => chip.textContent.trim());
            formTypes.forEach((expected) => {
                if (!actualFormTypes.includes(expected)) {
                    hasMismatch = true;
                    cy.log(`Missing Form Type: ${expected}`);
                }
            });
        });
        // Require Before Routing
        cy.get('td[aria-colindex="5"]')
            .invoke('text')
            .then((text) => {
            if (text.trim() !== requirebeforeRouting) {
                hasMismatch = true;
                cy.log(`Mismatch in Require Before Routing: expected "${requirebeforeRouting}", got "${text.trim()}"`);
            }
        });
        // Send to XR After Approval
        cy.get('td[aria-colindex="6"]')
            .invoke('text')
            .then((text) => {
            if (text.trim() !== sendToXRAfterApproval) {
                hasMismatch = true;
                cy.log(`Mismatch in Send to XR After Approval: expected "${sendToXRAfterApproval}", got "${text.trim()}"`);
            }
        });
        // Departments
        cy.get('td[aria-colindex="7"] .k-chip-label').then((chips) => {
            const actualDepartments = Cypress._.map(chips, (chip) => chip.textContent.trim());
            departments.forEach((expected) => {
                if (!actualDepartments.includes(expected)) {
                    hasMismatch = true;
                    cy.log(`Missing Department: ${expected}`);
                }
            });
        });
        // Final write to file
        cy.then(() => {
            cy.log(`Final Mismatch Result: ${hasMismatch}`);
            cy.writeFile('src/fixtures/tc106-result.json', { hasMismatch });
        });
    }
});
// Now read the result file and proceed conditionally
cy.readFile('src/fixtures/tc106-result.json').then((data) => {
    if (data.hasMismatch || data.isEmpty) {
        cy.purchaseOrderAdminManageOnboardingApproval({
            useFaker: false,
            visitPurchaseOrderAdminPage: '/fo/purchase-order/admin',
            selectAdminAction: 'Manage Onboarding Approvals',
            selectCustomer: this.projectDetails.customerName,
            selectProject: this.projectDetails.projectName
        });
        const poAdmin = new core_payroll_1.PurchaseOrderAdmin();
        if (data.hasMismatch) {
            poAdmin.clickDelete();
        }
        poAdmin.clickCreate();
        poAdmin.fillApprovalLevel('1');
        poAdmin.fillRole('Production Manager');
        poAdmin.fillUsers(this.users.reviewerEmail);
        poAdmin.fillFormType('Deal Memo');
        poAdmin.fillFormType('Offer');
        poAdmin.fillFormType('XR Start Form');
        poAdmin.fillFormType('Wage Theft Form');
        poAdmin.checkRequireBeforeRouting(false);
        poAdmin.checkSendToXRAfterApproval(true);
        poAdmin.fillDepartment('Costume');
        poAdmin.fillDepartment('Grips');
        poAdmin.fillDepartment('Office');
        poAdmin.fillDepartment('Production');
        // Known Defect workaround
        poAdmin.fillUsers(this.users.reviewerEmail);
        poAdmin.clickSave();
    }
    else {
        cy.log('No mismatch or missing data. Skipping creation.');
    }
});

