/*
      Module: Onboarding and Timecard
      Last Updated: Chandini(4/23/2025)
      High level steps:
      1. Login as Payee and create a time card.
      2. check functionality so that when user types in the hour, automatically update min to :00
 For example, when the user enters 8AM > they would enter > hour:min > 8: > and then system should automatically populate to :00 .
 If their true in time is 8:12AM, they can update the :00 with :12, but we want it to default to :00 for ALL time entry fields.
      3. check for automatic default the IN time to AM, however still allow the user to update to PM if and when necessary
      4. check to look to the MEAL IN time to default either AM or PM in the OUT time
     */
cy.userLoginNonAdmin();
cy.get('[data-cy="username"]', { timeout: 30000 }).should('be.visible').type(this.users.payeeEmail);
cy.get('[data-cy="next"]', { timeout: 30000 }).should('be.visible').click();
cy.get('[data-cy="password"]', { timeout: 30000 }).should('be.visible').type(this.users.payeePassword);
cy.get('[data-cy="login"]', { timeout: 20000 }).click({ force: true });
//Enter credentails to begin a time card.
const timeEntry = new core_payroll_1.TimeEntry();
timeEntry.visit();
timeEntry.getCreatingNewTimeCardEntryModal().waitApiLoadWhenOpen();
timeEntry.getCreatingNewTimeCardEntryModal().selectProjectName('18678');
timeEntry.getCreatingNewTimeCardEntryModal().selectOccupation('Non-Union ERPS - Art Director');
timeEntry.getCreatingNewTimeCardEntryModal().dateWeekEnding('2/1/2025');
timeEntry.getCreatingNewTimeCardEntryModal().clickBeginTimeEntry();
//Set the default settings before fiiling the time-entry
timeEntry.getTimeEntryDetails().waitForPageFirstLoad();
cy.wait(3000); //wait is necessary to load the elements in the page
timeEntry.getTimeEntryDetails().clickUserPreferences();
cy.contains('button', 'Set Default').click().wait(2000);
cy.contains('button', 'Update').click().wait(3000);
timeEntry.getTimeEntryDetails().checkStandard12HourAmPM();
//Fill in the time-entry credentails.
const inTime = '09:00 AM';
timeEntry.getTimeEntryDetails().fillDayEntryTable({ columnName: 'In', tableRow: 1, data: '9AM' });
timeEntry.getTimeEntryDetails().fillDayEntryTable({ columnName: 'Meal 1 In', tableRow: 1, data: '12:00 PM' });
timeEntry.getTimeEntryDetails().verifyFillDayEntryTable({ columnName: 'In', tableRow: 1, data: inTime });
if (inTime.includes('AM')) {
    cy.log('Default IN time is AM');
}
//User should able to change 12:00PM to 12:12PM.
timeEntry.getTimeEntryDetails().fillDayEntryTable({ columnName: 'Meal 1 In', tableRow: 1, data: '12:12 PM' });
timeEntry.getTimeEntryDetails().fillDayEntryTable({ columnName: 'Meal 1 Out', tableRow: 1, data: '1' });
timeEntry.getTimeEntryDetails().verifyFillDayEntryTable({ columnName: 'Meal 1 In', tableRow: 1, data: '12:12 PM' });
//Meal out time should have same default formate as meal in time that is if we give 12pm as meal in then 1 as meal out time it should take 1 pm as default.
timeEntry.getTimeEntryDetails().fillDayEntryTable({ columnName: 'Out', tableRow: 1, data: '7' });
timeEntry.getTimeEntryDetails().fillDayEntryTable({ columnName: 'In', tableRow: 2, data: '09:00 AM' });
timeEntry.getTimeEntryDetails().verifyFillDayEntryTable({ columnName: 'Meal 1 Out', tableRow: 1, data: '01:00 PM' });
timeEntry.getTimeEntryDetails().verifyFillDayEntryTable({ columnName: 'Out', tableRow: 1, data: '07:00 PM' });

