/*
Module: Onboarding & TimeCard Entry
Last Updated: Adlina (21/4/2025)
High Level Steps:
Part 1:
    1. Login as Payee with multiple project
    2. Go for timecard entry
    3. Check for the listed Project
*/
// Step 1 : Login as Payee with multiple project
cy.userLoginNonAdmin();
cy.visit(`${this.postfixURL.payeeOffer}`);
cy.get('[data-cy="username"]').should('be.visible').type(this.users.payeeEmail);
cy.get('[data-cy="next"]').click();
cy.get('[data-cy="password"]').should('be.visible').type(this.users.payeePassword);
cy.get('[data-cy="login"]').click();
cy.acceptPayeeTerms();
// Step 2: Collect projectoccupation pairs
let projectMap = {};
cy.get('tbody tr').then(($rows) => {
    $rows.each((index, row) => {
        const $row = Cypress.$(row);
        const project = $row.find('td').eq(1).text().trim();
        const occupation = $row.find('td').eq(2).text().trim();
        if (!projectMap[project]) {
            projectMap[project] = [];
        }
        if (!projectMap[project].includes(occupation)) {
            projectMap[project].push(occupation);
        }
    });
});
// Step 3: Open time entry and validate projectoccupation logic
cy.then(() => {
    const timeEntry = new core_payroll_1.TimeEntry();
    timeEntry.visit();
    cy.wait(3000);
    const modal = timeEntry.getCreatingNewTimeCardEntryModal();
    Object.entries(projectMap).forEach(([projectName, expectedOccupations]) => {
        modal.selectProjectName(projectName);
        cy.wait(1000);
        cy.get('[data-cy="occupation-dropdownlist"]').click();
        expectedOccupations.forEach((occupation) => {
            cy.contains('.k-list-item', occupation).should('exist');
        });
        cy.get('body').click(0, 0);
    });
});

