/*
Module: Onboarding and Timecard
Last Updated: Adlina (5/2/2025)
High level steps:
    1. Login as Production Manager
    2. Check for the hotfilters and interact with it - Default filters should be pending my approval
    3. Check for "Calculation Status" is available in between "Amount" and "Approval Status"
    4. Check for the UI - Union Field and Payee Name is visible and interact with it
*/
// Step 1: Login as Production Manager
cy.userLoginNonAdmin();
cy.visit(`${this.postfixURL.offer}`);
cy.get('[data-cy="username"]', { timeout: 60000 }).type(this.users.productionmanagerEmail);
cy.get('[data-cy="next"]', { timeout: 60000 }).click();
cy.get('[data-cy="password"]', { timeout: 60000 }).type(this.users.productionmanagerPassword);
cy.get('[data-cy="login"]', { timeout: 60000 }).click();
cy.acceptTerms();
const timeEntry = new core_payroll_1.TimeEntry();
timeEntry.visit();
// Step 2: Check for the hotfilters and interact with it - Default filters should be pending my approval - To reconfirm bugs
// cy.get('[data-cy="filter-button-group"]', { timeout: 60000 }).contains('Pending My Approval')
//   .closest('button').should('have.class', 'k-selected');
// Step 3: Check for "Calculation Status" is available in between "Amount" and "Approval Status"
cy.get('[role="row"][aria-rowindex="1"]', { timeout: 60000 })
    .find('th')
    .then(($headers) => {
    const headerTexts = [...$headers].map((el) => el.innerText.trim());
    const amountIndex = headerTexts.indexOf('Amount');
    const calcStatusIndex = headerTexts.indexOf('Calculation Status');
    const approvalStatusIndex = headerTexts.indexOf('Approval Status');
    expect(amountIndex).to.be.greaterThan(-1);
    expect(calcStatusIndex).to.be.greaterThan(-1);
    expect(approvalStatusIndex).to.be.greaterThan(-1);
    // Ensure the order is: Amount < Calculation Status < Approval Status
    expect(amountIndex).to.be.lessThan(calcStatusIndex);
    expect(calcStatusIndex).to.be.lessThan(approvalStatusIndex);
});
// Step 4: Check for the UI - Union Field and Payee Name is visible and interact with it
// Union
cy.get('[name="unionsList"]', { timeout: 15000 }).should('be.visible');
timeEntry.selectUnion('Non-Union ERPS');
timeEntry.clickSearch();
cy.wait(3000); // Manual wait to ensure all element loads correctly
cy.get('[role="row"]')
    .not('[aria-rowindex="1"]')
    .each(($row) => {
    cy.wrap($row).find('td').eq(6).invoke('text').should('eq', 'Non-Union ERPS');
});
// Clear field, check for payee name and store before search for it
timeEntry.clickClear();
cy.get('[role="row"]')
    .not('[aria-rowindex="1"]')
    .first()
    .find('td')
    .eq(3)
    .invoke('text')
    .then((name) => {
    cy.wrap(name.trim()).as('savedName');
});
cy.get('[role="row"]')
    .not('[aria-rowindex="1"]')
    .first()
    .find('td')
    .eq(3)
    .invoke('text')
    .then((name) => {
    cy.get('@savedName').then((savedName) => {
        timeEntry.selectName(savedName); // Use the saved name for selection
        timeEntry.clickSearch();
        cy.wait(3000);
        cy.get('[role="row"]')
            .not('[aria-rowindex="1"]')
            .each(($row) => {
            cy.wrap($row).find('td').eq(3).invoke('text').should('eq', savedName);
        });
    });
});

