// Load the fixture data
cy.fixture('xr-login.json').then(({ username, password, payeesearch, notification_page }) => {
    // Log in to XR Pay using the fixture credentials
    cy.userLoginXR(username, password);
    // Visit the Payee search
    cy.visit(payeesearch);
    cy.wait(8000); // wait is necessary to load the page
    //sent invitation to payee portal
    cy.get('kendo-dropdownlist[name="payeeTypeSelect"]')
        .click()
        .then(() => {
        cy.get('.k-list-item').contains('Individual').click({ force: true });
    });
    cy.intercept('https://app.uat2-extremereach.com/api/payroll/payee-search/payees/search').as('pageload');
    cy.get('[data-cy="search-submit-btn"]').click();
    cy.wait('@pageload');
    // Get the current row and find the link to click
    cy.get('kendo-grid .k-table .k-master-row')
        .eq(0) // Cycle through 0, 1, 2, 3 (0 is the first row)
        .find('a')
        .first()
        .click();
    cy.wait(5000); // wait is necessary to load data of page.
    cy.get('.details-form-actions > [name="buttonEdit"] > .fa').click();
    // Generate a random email with faker
    const randomEmail = faker_1.faker.internet.email();
    // Modify the email to have the desired prefix and suffix
    const modifiedEmail = randomEmail.split('@')[0] + '+pp@extremereach.com'; // Modify the local part with '+pp' and use the fixed domain
    cy.wait(3000);
    cy.get('[name="details.email"]').clear().type(modifiedEmail);
    cy.get('[name="details.phone"]').clear().type('2122010857');
    cy.get('#ethnicity').select('Not Given');
    cy.get('#datepicker-1') // Locate the input field
        .click() // Open the date picker
        .clear() // Clear the existing value if any
        .type('04/22/2002'); // Type the date in the input field
    cy.get('[name="buttonSave"]').click();
    cy.get('[name="buttonInviteToPayeePortal"]').click();
    cy.visit(notification_page);
    cy.contains('h1', 'Search Notifications', { timeout: 60000 }).should('be.visible');
    cy.get('#notificationTypeId', { timeout: 20000 }).should('be.visible');
    cy.get('.sendbutton').click();
    cy.get('#pageSize', { timeout: 20000 }).should('be.visible');
    const timeout = 5 * 60 * 1000; // 5 minutes in milliseconds
    const startTime = Date.now(); // Track the start time of the loop
    let emailFound = false;
    // Function to check for the email
    function checkForEmail() {
        cy.get('tbody') // Target the table body
            .find('tr') // Look for rows in the table
            .each(($row) => {
            // Check if any row contains the email in the "To" column (5th td)
            if ($row.find('td').eq(4).text().includes(modifiedEmail)) {
                emailFound = true;
                cy.log(`Email  found in the table!`);
                return false; // Exit the `.each()` loop when the email is found
            }
        })
            .then(() => {
            // If the email is found, stop further retries
            if (emailFound) {
                cy.log('Stopping the loop as the email was found.');
            }
            else if (Date.now() - startTime < timeout) {
                // If the email is not found and the timeout is not reached, retry
                cy.get('.sendbutton').click(); // Click the search button again
                cy.wait(5000).then(() => {
                    // 5-second wait before retrying
                    checkForEmail(); // Recursive call to check again
                });
            }
            else {
                // If the timeout is reached and email is not found
                cy.log('Email not found after 5 minutes.');
            }
        });
    }
    // Start the email checking loop
    checkForEmail();
});

